import { useState, useEffect } from "react";
import axios from "axios";

function Profile({ user, setUser }) {
  const token = localStorage.getItem("token");

  const [username, setUsername] = useState(user ? user.username : "");
  const [email, setEmail] = useState(user ? user.email : "");
  const [password, setPassword] = useState("");

  // Optionnel : refetch des données pour s’assurer qu’elles sont à jour
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/api/user/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsername(res.data.username);
        setEmail(res.data.email);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProfile();
  }, [token]);

  const handleUpdate = async () => {
    try {
      await axios.put(
        "http://127.0.0.1:5000/api/user/me",
        { username, email, password: password || undefined },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Informations mises à jour !");
      // Mettre à jour le state user local si nécessaire
      setUser({ ...user, username, email });
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la mise à jour des informations");
    }
  };

  const handleDelete = async () => {
    if (!window.confirm("Voulez-vous vraiment supprimer votre compte ?"))
      return;
    try {
      await axios.delete("http://127.0.0.1:5000/api/user/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Compte supprimé");
      localStorage.removeItem("token");
      setUser(null);
      // Redirection vers /login ou /register selon vos préférences
      window.location.href = "/register";
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la suppression du compte");
    }
  };

  return (
    <div>
      <h2>Mon Profil</h2>
      <label>Username:</label>
      <input value={username} onChange={(e) => setUsername(e.target.value)} />
      <br />
      <label>Email:</label>
      <input value={email} onChange={(e) => setEmail(e.target.value)} />
      <br />
      <label>Nouveau mot de passe (optionnel):</label>
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <br />
      <button onClick={handleUpdate}>Mettre à jour</button>
      <button onClick={handleDelete} style={{ color: "red" }}>
        Supprimer mon compte
      </button>
    </div>
  );
}

export default Profile;
