// frontend/src/pages/Ads.js
import { useState, useEffect } from "react";
import axios from "axios";

function Ads({ user }) {
  const [ads, setAds] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [category, setCategory] = useState("");

  const token = localStorage.getItem("token");

  const fetchAds = async () => {
    const res = await axios.get("http://127.0.0.1:5000/api/ads");
    setAds(res.data);
  };

  useEffect(() => {
    fetchAds();
  }, []);

  const createAd = async () => {
    try {
      await axios.post(
        "http://127.0.0.1:5000/api/ads",
        { title, description, price, category },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      await fetchAds();
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la création de l'annonce");
    }
  };

  const updateAd = async (id) => {
    const newTitle = prompt("Nouveau titre ?");
    if (!newTitle) return;
    try {
      await axios.put(
        `http://127.0.0.1:5000/api/ads/${id}`,
        { title: newTitle },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchAds();
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la mise à jour");
    }
  };

  const deleteAd = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:5000/api/ads/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchAds();
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la suppression");
    }
  };

  return (
    <div>
      <h2>Liste des Annonces</h2>
      {user && (
        <div>
          <h3>Créer une nouvelle annonce</h3>
          <input
            placeholder="Titre"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            placeholder="Prix"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <input
            placeholder="Catégorie"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
          <button onClick={createAd}>Créer</button>
        </div>
      )}
      <ul>
        {ads.map((ad) => (
          <li key={ad._id}>
            <h4>{ad.title}</h4>
            <p>{ad.description}</p>
            <p>Prix: {ad.price}€</p>
            <p>Catégorie: {ad.category}</p>
            <p>Auteur: {ad.author?.username}</p>
            {user && (
              <>
                <button onClick={() => updateAd(ad._id)}>Modifier</button>
                <button onClick={() => deleteAd(ad._id)}>Supprimer</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Ads;
