// frontend/src/pages/Login.js
import { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom"; // Import de useHistory
import { Alert } from "react-bootstrap"; // Optionnel : pour des alertes Bootstrap

function Login({ setUser }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Pour gérer les erreurs

  const history = useHistory(); // Initialisation du hook

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://127.0.0.1:5000/api/auth/login", {
        email,
        password,
      });
      localStorage.setItem("token", res.data.token);
      setUser({
        username: res.data.user.username,
        email: res.data.user.email,
        id: res.data.user.id,
        role: res.data.user.role,
      });
      history.push("/ads"); // Redirection vers la page des annonces
    } catch (err) {
      console.error(err);
      setError("Erreur de connexion. Veuillez vérifier vos identifiants.");
    }
  };

  return (
    <div className="container mt-5">
      <div className="card p-4 shadow-sm">
        <h2 className="mb-4">Connexion</h2>
        {error && <Alert variant="danger">{error}</Alert>}{" "}
        {/* Affichage des erreurs */}
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input
            className="form-control"
            type="email"
            placeholder="Votre email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Mot de passe</label>
          <input
            className="form-control"
            type="password"
            placeholder="Votre mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="btn btn-primary w-100" onClick={handleLogin}>
          Se connecter
        </button>
      </div>
    </div>
  );
}

export default Login;
