// frontend/src/pages/Admin.js
import { useState, useEffect } from "react";
import axios from "axios";

function Admin() {
  const [users, setUsers] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/api/admin/users", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsers(res.data);
      } catch (err) {
        console.error(err);
        alert("Erreur lors de la récupération des utilisateurs");
      }
    };
    fetchUsers();
  }, [token]);

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:5000/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter((u) => u._id !== id));
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la suppression");
    }
  };

  return (
    <div>
      <h2>Administration - Gestion des Utilisateurs</h2>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.username} - {user.email} - Rôle: {user.role}
            <button onClick={() => deleteUser(user._id)}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Admin;
