// frontend/src/pages/Ads.js
import { useState, useEffect } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

function Ads({ user }) {
  const [ads, setAds] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [category, setCategory] = useState("");

  const [showEditModal, setShowEditModal] = useState(false);
  const [currentAd, setCurrentAd] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editPrice, setEditPrice] = useState("");
  const [editCategory, setEditCategory] = useState("");

  const token = localStorage.getItem("token");

  const fetchAds = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:5000/api/ads", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAds(res.data);
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la récupération des annonces");
    }
  };

  useEffect(() => {
    fetchAds();
    // eslint-disable-next-line
  }, []);

  const createAd = async () => {
    if (!title || !description || !price) {
      alert("Veuillez remplir tous les champs obligatoires.");
      return;
    }

    try {
      await axios.post(
        "http://127.0.0.1:5000/api/ads",
        { title, description, price, category },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      await fetchAds();
      setTitle("");
      setDescription("");
      setPrice("");
      setCategory("");
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la création de l'annonce");
    }
  };

  const openEditModal = (ad) => {
    setCurrentAd(ad);
    setEditTitle(ad.title);
    setEditDescription(ad.description);
    setEditPrice(ad.price);
    setEditCategory(ad.category);
    setShowEditModal(true);
  };

  const handleEditAd = async () => {
    if (!editTitle || !editDescription || !editPrice) {
      alert("Veuillez remplir tous les champs obligatoires.");
      return;
    }

    try {
      await axios.put(
        `http://127.0.0.1:5000/api/ads/${currentAd._id}`,
        {
          title: editTitle,
          description: editDescription,
          price: editPrice,
          category: editCategory,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setShowEditModal(false);
      setCurrentAd(null);
      await fetchAds();
      alert("Annonce mise à jour avec succès !");
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la mise à jour de l'annonce");
    }
  };

  const deleteAd = async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer cette annonce ?"))
      return;
    try {
      await axios.delete(`http://127.0.0.1:5000/api/ads/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchAds();
      alert("Annonce supprimée avec succès !");
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la suppression");
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Liste des Annonces</h2>
      {user && (
        <div className="card p-4 mb-4">
          <h3>Créer une nouvelle annonce</h3>
          <Form>
            <Form.Group className="mb-3" controlId="formTitle">
              <Form.Label>Titre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Titre"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formPrice">
              <Form.Label>Prix (€)</Form.Label>
              <Form.Control
                type="number"
                placeholder="Prix"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formCategory">
              <Form.Label>Catégorie</Form.Label>
              <Form.Control
                type="text"
                placeholder="Catégorie"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              />
            </Form.Group>

            <Button variant="success" onClick={createAd}>
              <i className="fas fa-plus me-2"></i> Créer
            </Button>
          </Form>
        </div>
      )}

      <div className="row">
        {ads.length > 0 ? (
          ads.map((ad) => (
            <div className="col-md-4 mb-4" key={ad._id}>
              <div className="card h-100 shadow-sm">
                <div className="card-body">
                  <h4 className="card-title">{ad.title}</h4>
                  <p className="card-text">{ad.description}</p>
                  <p className="card-text">
                    <strong>Prix:</strong> {ad.price}€
                  </p>
                  <p className="card-text">
                    <strong>Catégorie:</strong> {ad.category}
                  </p>
                  <p className="card-text">
                    <strong>Auteur:</strong> {ad.author?.username}
                  </p>
                </div>
                {user && (
                  <div className="card-footer d-flex justify-content-between">
                    <Button
                      variant="warning"
                      size="sm"
                      onClick={() => openEditModal(ad)}
                    >
                      <i className="fas fa-edit me-1"></i> Modifier
                    </Button>
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => deleteAd(ad._id)}
                    >
                      <i className="fas fa-trash-alt me-1"></i> Supprimer
                    </Button>
                  </div>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="col-12 text-center">
            <p>Aucune annonce trouvée.</p>
          </div>
        )}
      </div>

      {/* Modal d'édition */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Modifier l'Annonce</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {currentAd && (
            <Form>
              <Form.Group className="mb-3" controlId="editFormTitle">
                <Form.Label>Titre</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Titre"
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="editFormDescription">
                <Form.Label>Description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  placeholder="Description"
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="editFormPrice">
                <Form.Label>Prix (€)</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Prix"
                  value={editPrice}
                  onChange={(e) => setEditPrice(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="editFormCategory">
                <Form.Label>Catégorie</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Catégorie"
                  value={editCategory}
                  onChange={(e) => setEditCategory(e.target.value)}
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>
            Annuler
          </Button>
          <Button variant="primary" onClick={handleEditAd}>
            <i className="fas fa-save me-2"></i> Enregistrer
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Ads;
